import * as React from 'react';
import { ProColumns } from "@ant-design/pro-table";
import { Button, message, Popconfirm, Space, Table, Tooltip, Image } from 'antd';
import 'antd/lib/image/style/index.less'
import _ from "lodash";
import EditForm from '../components/edit_form';

export const imgFields = ['image', 'cover'];
export function genColumns({schema, reGetData, collectionName, remove}) {

  let columns: ProColumns[] = []
  let index = 0;
  _.forIn(schema.properties, function (value, key) {
    let obj: any = {
      title: value.title,
      dataIndex: key,
      valueType: value.type,
      search: value.type === 'string' ? true : false,
      hideInTable: index > 4 ? true : false
    }
    if (_.indexOf(imgFields, key) > -1) {
      obj.render = (_, row, index,) => {
        if (row[key] && row[key].length > 0) {
          return (<Image
            width={100}
            src={`${row[key][0].url}?imageView2/5/w/100/h/100`}
            preview={{
              src: `${row[key][0].url}`,
            }}
          />
          )
        }
        return (<Image
          width={100}
          height={100}
          src="error"
          fallback="data:image/png;base64,"
        />)
      }
    }
    columns.push(obj);
    index++
  });

  columns.push({
    title: (
      <>
        创建时间
      </>
    ),
    width: 200,
    key: 'created',
    dataIndex: 'created',
    valueType: 'dateTime',
    sorter: true,
    search: false
  })

  columns.push({
    title: "操作",
    valueType: "option",
    width: 100,
    render: (text, record, _, action) => [
      <EditForm key={`link_edit_${record._id}`} id={record._id} schema={schema} title="编辑" collectionName={collectionName} onSuccess={reGetData}>
        <a>
          编辑
        </a>
      </EditForm>
      ,
      <Popconfirm
        key="link_del"
        placement="topRight"
        title={
          <div style={{ marginLeft: "8px" }}>
            <h4>删除数据</h4>
            <label style={{ color: "red" }}>
              将删除此数据，且删除后不可恢复
            </label>
            ，你还要继续吗？
          </div>
        }
        onConfirm={async () => {
          remove(record._id, () => {
            message.success("删除成功");
            reGetData();
          })
        }}
      >
        <a>删除</a>
      </Popconfirm>,
    ]
  })

  return columns
}